import React, { useState } from "react";

import "./debitCard.scss";

const initialValues = {
  name: "",
  cardNumber: "",
  expiredDate: "",
  cvv: "",
};

function Debitcard() {
  const [debitCardList, setDebitCardList] = useState([]);
  const [formDebit, setFormDebit] = useState(initialValues);

  const handleChange = (event) => {
    const { name, value } = event.target;

    setFormDebit({
      ...formDebit,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    setDebitCardList([...debitCardList, formDebit]);
    setFormDebit(initialValues);
  };

  return (
    <div className="container">
      {/* <div className="debit-card-preview">
        <div className="debit-card-name"></div>
      </div> */}
      <form onSubmit={handleSubmit}>
        <div className="debit-card-form">
          <input
            name="name"
            type="text"
            className="debit-card-input"
            placeholder="Name"
            onChange={handleChange}
            required
          />
          <input
            name="cardNumber"
            type="number"
            className="debit-card-input"
            placeholder="Card Number"
            onChange={handleChange}
            required
          />
          <div className="debit-card-sub-info">
            <input
              name="expiredDate"
              type="text"
              className="debit-card-input flex-45"
              placeholder="Expired Date"
              onChange={handleChange}
              required
            />
            <input
              name="cvv"
              type="text"
              className="debit-card-input flex-45"
              placeholder="CVV"
              onChange={handleChange}
              required
            />
          </div>
        </div>
        <button type="submit" className="submit-button">
          Submit
        </button>
      </form>
      <div className="debit-member-list">
        {debitCardList.map((record, index) => (
          <div className="debit-member-item">
            <p>{index + 1}</p>
            <p>{record.name}</p>
            <p>{record.cardNumber}</p>
            <p>{record.expiredDate}</p>
            <p>{record.cvv}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Debitcard;
