import React, { useState } from "react";

import "./debitCard.scss";

function Debitcard() {
  const [debitCardList, setDebitCardList] = useState([]);
  const [formDebit, setFormDebit] = useState({
    name: "",
    cardNumber: "",
    expiredDate: "",
    cvv: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;

    setFormDebit({
      ...formDebit,
      [name]: value,
    });
  };

  return (
    <div className="container">
      {/* <div className="debit-card-preview">
        <div className="debit-card-name"></div>
      </div> */}
      <form>
        <div className="debit-card-form">
          <input
            name="name"
            type="text"
            className="debit-card-input"
            placeholder="Name"
            onChange={handleChange}
          />
          <input
            name="cardNumber"
            type="number"
            className="debit-card-input"
            placeholder="Card Number"
            onChange={handleChange}
          />
          <div className="debit-card-sub-info">
            <input
              name="expiredDate"
              type="text"
              className="debit-card-input flex-45"
              placeholder="Expired Date"
              onChange={handleChange}
            />
            <input
              name="cvv"
              type="text"
              className="debit-card-input flex-45"
              placeholder="Expired Date"
              onChange={handleChange}
            />
          </div>
        </div>
      </form>
    </div>
  );
}

export default Debitcard;
