import './menu.scss'

// Requirements:
/*
1. Fetch menus from http://localhost:4040/menus

2. Using <ul> <li> render accordion menu. Refers to: https://cdn.hashnode.com/res/hashnode/image/upload/v1632910257498/EuFFg0l3d.gif

3. Default first item will be expanded

4. When click to expand another item, all remain items will be collapsed

5. Child elements will be indented 8px

6. Can using button text instead + / - icons
  - + => expand
  - - => collapse

7. Using SCSS, no inline styling
*/

function Menu() {
  return <div>Menu</div>;
}

export default Menu;
